  --font-serif: 'Georgia', serif;
  --font-mono: 'Menlo', monospace;
  --radius: 1rem;
  --shadow-2xs: 0px 2px 0px 0px hsl(168, 76%, 36%, 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(168, 76%, 36%, 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(168, 76%, 36%, 0.00), 0px 1px 2px -1px hsl(168, 76%, 36%, 0.00);
  --shadow: 0px 2px 0px 0px hsl(168, 76%, 36%, 0.00), 0px 1px 2px -1px hsl(168, 76%, 36%, 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(168, 76%, 36%, 0.00), 0px 2px 4px -1px hsl(168, 76%, 36%, 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(168, 76%, 36%, 0.00), 0px 4px 6px -1px hsl(168, 76%, 36%, 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(168, 76%, 36%, 0.00), 0px 8px 10px -1px hsl(168, 76%, 36%, 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(168, 76%, 36%, 0.00);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

.progress-ring {
  transform: rotate(-90deg);
}

.progress-ring-fill {
  transition: stroke-dashoffset 0.5s ease-in-out;
}

.floating-animation {
  animation: float 3s ease-in-out infinite;
}

@keyframes float {
  0%, 100% { 
    transform: translateY(0px); 
  }
  50% { 
    transform: translateY(-10px); 
  }
}

.eco-gradient {
  background: linear-gradient(135deg, var(--primary), var(--secondary));
}

.glass-effect {
  backdrop-filter: blur(10px);
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

