client/src/components/GuildCard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Link } from "wouter";

interface GuildCardProps {
  user: {
    guildId?: string;
  };
}

export default function GuildCard({ user }: GuildCardProps) {
  const { data: guild, isLoading } = useQuery({
    queryKey: ["/api/guild", user.guildId],
    enabled: !!user.guildId,
    retry: false,
  });

  if (isLoading) {
    return (
      <div className="bg-card rounded-lg border border-border p-6">
        <div className="animate-pulse space-y-4">
          <div className="h-4 bg-muted rounded w-1/2"></div>
          <div className="h-20 bg-muted rounded"></div>
        </div>
      </div>
    );
  }

  if (!user.guildId || !guild) {
    return (
      <div className="bg-card rounded-lg border border-border p-6">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-xl font-bold">Join a Guild</h3>
          <i className="fas fa-users text-primary text-xl"></i>
        </div>

        <div className="text-center space-y-4">
          <div className="w-20 h-20 bg-muted/50 rounded-full flex items-center justify-center mx-auto">
            <i className="fas fa-users text-3xl text-muted-foreground"></i>
          </div>
          <div>
            <h4 className="font-semibold mb-2">No Guild Yet</h4>
            <p className="text-muted-foreground text-sm mb-4">
              Join a guild to collaborate with other eco champions and participate in team challenges.
            </p>
          </div>
          <Link href="/guilds" data-testid="link-join-guild">
            <Button>
              <i className="fas fa-plus mr-2"></i>
              Find Guild
            </Button>
          </Link>
        </div>
      </div>
    );
  }

  const challengeProgress = (guild as any)?.challengeProgress || 0;
  const challengeTarget = (guild as any)?.challengeTarget || 100;
  const progressPercentage = Math.round((challengeProgress / challengeTarget) * 100);

  return (
    <div className="bg-card rounded-lg border border-border p-6">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-xl font-bold">Your Guild</h3>
        <i className="fas fa-users text-primary text-xl"></i>
      </div>

      <div className="text-center mb-6">
        <div className="w-20 h-20 eco-gradient rounded-full flex items-center justify-center mx-auto mb-3">
          <i className="fas fa-tree text-3xl text-white"></i>
        </div>
        <h4 className="text-lg font-bold" data-testid="text-guild-name">
          {(guild as any)?.name}
        </h4>
        <p className="text-muted-foreground" data-testid="text-guild-members">
          {(guild as any)?.memberCount} members
        </p>
      </div>

      <div className="space-y-4">
        <div className="flex justify-between items-center">
          <span className="text-sm font-medium">Guild Progress</span>
          <span className="text-sm text-muted-foreground" data-testid="text-guild-progress">
            {progressPercentage}%
          </span>
        </div>
        <div className="w-full bg-muted rounded-full h-3">
          <div 
            className="bg-primary h-3 rounded-full transition-all duration-500" 
            style={{ width: `${progressPercentage}%` }}
          ></div>
        </div>

        {(guild as any)?.currentChallenge && (
          <div className="bg-muted/50 rounded-lg p-4">
            <h5 className="font-semibold mb-2">Current Challenge</h5>
            <p className="text-sm text-muted-foreground" data-testid="text-guild-challenge">
              {(guild as any)?.currentChallenge} - {challengeProgress}/{challengeTarget} completed
            </p>
            {(guild as any)?.challengeDeadline && (
              <div className="mt-2 flex items-center space-x-2">
                <i className="fas fa-clock text-accent"></i>
                <span className="text-sm">
                  {new Date((guild as any).challengeDeadline).toLocaleDateString()}
                </span>
              </div>
            )}
          </div>
        )}

        <Link href="/guilds" data-testid="link-guild-details">
          <Button variant="outline" className="w-full">
            View Guild Details
          </Button>
        </Link>
      </div>
    </div>
  );
}

client/src/components/LeaderboardCard.ts.content:
interface LeaderboardCardProps {
  leaderboard: Array<{
    userId: string;
    xp: number;
    rank: number;
    user?: {
      firstName?: string;
      lastName?: string;
      level: number;
    };
  }>;
  currentUserId: string;
}

export default function LeaderboardCard({ leaderboard, currentUserId }: LeaderboardCardProps) {
  return (
    <div className="bg-card rounded-lg border border-border p-6">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-xl font-bold">Leaderboard</h3>
        <select className="bg-muted rounded px-3 py-1 text-sm border border-border" data-testid="select-leaderboard-period">
          <option value="weekly">This Week</option>
          <option value="monthly">This Month</option>
          <option value="all_time">All Time</option>
        </select>
      </div>

      <div className="space-y-3">
        {leaderboard.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            <i className="fas fa-chart-line text-4xl mb-4 opacity-50"></i>
            <p>No leaderboard data available</p>
          </div>
        ) : (
          leaderboard.slice(0, 4).map((entry, index) => {
            const isCurrentUser = entry.userId === currentUserId;
            const rankColor = index === 0 ? "bg-accent" : index === 1 ? "bg-secondary" : index === 2 ? "bg-primary/70" : "bg-muted";

            return (
              <div 
                key={entry.userId}
                className={`flex items-center justify-between p-3 rounded-lg ${
                  isCurrentUser ? "bg-secondary/10 border border-secondary/20" : "bg-muted/30"
                }`}
                data-testid={`leaderboard-entry-${index + 1}`}
              >
                <div className="flex items-center space-x-3">
                  <div className={`w-8 h-8 ${rankColor} rounded-full flex items-center justify-center text-sm font-bold ${
                    index < 3 ? "text-white" : "text-foreground"
                  }`}>
                    {entry.rank || index + 1}
                  </div>
                  <div>
                    <div className="font-medium" data-testid={`text-leaderboard-name-${index + 1}`}>
                      {entry.user?.firstName || "Anonymous"} {isCurrentUser ? "(You)" : ""}
                    </div>
                    <div className="text-sm text-muted-foreground">
                      Level {entry.user?.level || 1}
                    </div>
                  </div>
                </div>
                <div className="text-right">
                  <div className="font-bold" data-testid={`text-leaderboard-xp-${index + 1}`}>
                    {entry.xp.toLocaleString()}
                  </div>
                  <div className="text-sm text-muted-foreground">XP</div>
                </div>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

client/src/components/LessonCard.tsx.content:
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface LessonCardProps {
  lesson: {
    id: number;
    title: string;
    description: string;
    track: string;
    difficulty: string;
    xpReward: number;
    creditReward: number;
    duration: number;
    imageUrl?: string;
  };
  progress?: {
    completed: boolean;
    score?: number;
  };
}

const trackColors = {
  EcoExplorer: "bg-primary/10 text-primary",
  ClimateChampion: "bg-secondary/10 text-secondary", 
  WasteWarrior: "bg-accent/10 text-accent",
  GreenInnovator: "bg-primary/10 text-primary",
};

const difficultyColors = {
  beginner: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
  intermediate: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
  advanced: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
};

export default function LessonCard({ lesson, progress }: LessonCardProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const completeLesson = useMutation({
    mutationFn: async () => {
      await apiRequest("POST", "/api/progress", {
        lessonId: lesson.id,
        completed: true,
        score: 85 + Math.floor(Math.random() * 15), // Simulate random score 85-100
      });
    },
    onSuccess: () => {
      toast({
        title: "Lesson Completed!",
        description: `You earned ${lesson.xpReward} XP and ${lesson.creditReward} EcoCredits`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/progress"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to complete lesson",
        variant: "destructive",
      });
    },
  });

  const trackColor = trackColors[lesson.track as keyof typeof trackColors] || "bg-gray-100 text-gray-800";
  const difficultyColor = difficultyColors[lesson.difficulty as keyof typeof difficultyColors] || difficultyColors.beginner;
  const isCompleted = progress?.completed;

  return (
    <div className="bg-card rounded-lg border border-border overflow-hidden hover:shadow-md transition-shadow">
      {lesson.imageUrl && (
        <img 
          src={lesson.imageUrl} 
          alt={lesson.title}
          className="w-full h-48 object-cover"
        />
      )}

      <div className="p-6">
        <div className="flex items-center justify-between mb-3">
          <Badge className={trackColor} data-testid={`badge-track-${lesson.id}`}>
            {lesson.track}
          </Badge>
          <div className="flex items-center space-x-2">
            <Badge variant="outline" className={difficultyColor} data-testid={`badge-difficulty-${lesson.id}`}>
              {lesson.difficulty}
            </Badge>
            {isCompleted && (
              <Badge className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200" data-testid={`badge-completed-${lesson.id}`}>
                <i className="fas fa-check mr-1"></i>
                Completed
              </Badge>
            )}
          </div>
        </div>

        <h4 className="text-lg font-semibold mb-2" data-testid={`text-lesson-title-${lesson.id}`}>
          {lesson.title}
        </h4>

        <p className="text-muted-foreground text-sm mb-4" data-testid={`text-lesson-description-${lesson.id}`}>
          {lesson.description}
        </p>

        {progress?.score && (
          <div className="mb-4">
            <div className="flex justify-between text-sm mb-1">
              <span>Your Score</span>
              <span className="font-semibold">{progress.score}%</span>
            </div>
            <div className="w-full bg-muted rounded-full h-2">
              <div 
                className="bg-primary h-2 rounded-full" 
                style={{ width: `${progress.score}%` }}
              ></div>
            </div>
          </div>
        )}

        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4 text-sm text-muted-foreground">
            <span data-testid={`text-lesson-duration-${lesson.id}`}>
              <i className="fas fa-clock mr-1"></i>{lesson.duration} min
            </span>
            <span data-testid={`text-lesson-xp-${lesson.id}`}>
              <i className="fas fa-certificate mr-1"></i>+{lesson.xpReward} XP
            </span>
            <span data-testid={`text-lesson-credits-${lesson.id}`}>
              <i className="fas fa-coins mr-1"></i>+{lesson.creditReward}
            </span>
          </div>

          <Button
            onClick={() => completeLesson.mutate()}
            disabled={isCompleted || completeLesson.isPending}
            size="sm"
            data-testid={`button-lesson-action-${lesson.id}`}
          >
            {isCompleted ? "Completed" : completeLesson.isPending ? "Starting..." : "Start Lesson"}
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/MissionCard.tsx.content:
import { Button } from "@/components/ui/button";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface MissionCardProps {
  mission: {
    id: number;
    title: string;
    description: string;
    type: string;
    track?: string;
    xpReward: number;
    creditReward: number;
    status?: string;
    progress?: number;
  };
  isFeatured?: boolean;
}

export default function MissionCard({ mission, isFeatured = false }: MissionCardProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const startMissionMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("POST", `/api/missions/${mission.id}/start`);
    },
    onSuccess: () => {
      toast({
        title: "Mission Started!",
        description: `You've started: ${mission.title}`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to start mission",
        variant: "destructive",
      });
    },
  });

  const isCompleted = mission.status === "completed";
  const isInProgress = mission.status === "in_progress";
  const canStart = !isCompleted && !isInProgress;

  if (isFeatured) {
    return (
      <div className="bg-gradient-to-r from-primary to-secondary rounded-lg p-6 text-white">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-xl font-bold">Today's Mission</h3>
          <i className="fas fa-flag text-2xl"></i>
        </div>
        <h4 className="text-lg font-semibold mb-2" data-testid="text-featured-mission-title">
          {mission.title}
        </h4>
        <p className="mb-4 opacity-90" data-testid="text-featured-mission-description">
          {mission.description}
        </p>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <div className="bg-white/20 rounded-full px-3 py-1 text-sm">
              <i className="fas fa-star mr-1"></i>
              +{mission.xpReward} XP
            </div>
            <div className="bg-white/20 rounded-full px-3 py-1 text-sm">
              <i className="fas fa-coins mr-1"></i>
              +{mission.creditReward} EcoCredits
            </div>
          </div>
          <Button
            onClick={() => startMissionMutation.mutate()}
            disabled={!canStart || startMissionMutation.isPending}
            className="bg-white text-primary px-4 py-2 rounded-lg font-semibold hover:bg-white/90 transition-colors"
            data-testid="button-start-featured-mission"
          >
            {isCompleted ? "Completed" : isInProgress ? "In Progress" : "Start Mission"}
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-card rounded-lg border border-border p-6 hover:shadow-md transition-shadow">
      <div className="flex items-start justify-between mb-4">
        <div className="flex-1">
          <div className="flex items-center space-x-2 mb-2">
            <span className={`px-2 py-1 text-xs font-medium rounded-full ${
              mission.type === "daily" ? "bg-primary/10 text-primary" :
              mission.type === "weekly" ? "bg-secondary/10 text-secondary" :
              "bg-accent/10 text-accent"
            }`}>
              {mission.type}
            </span>
            {mission.track && (
              <span className="px-2 py-1 text-xs font-medium rounded-full bg-muted text-muted-foreground">
                {mission.track}
              </span>
            )}
          </div>
          <h4 className="text-lg font-semibold mb-2" data-testid={`text-mission-title-${mission.id}`}>
            {mission.title}
          </h4>
          <p className="text-muted-foreground text-sm mb-4" data-testid={`text-mission-description-${mission.id}`}>
            {mission.description}
          </p>
        </div>
      </div>

      {isInProgress && mission.progress !== undefined && (
        <div className="mb-4">
          <div className="flex justify-between text-sm mb-1">
            <span>Progress</span>
            <span>{mission.progress}%</span>
          </div>
          <div className="w-full bg-muted rounded-full h-2">
            <div 
              className="bg-primary h-2 rounded-full transition-all duration-500" 
              style={{ width: `${mission.progress}%` }}
            ></div>
          </div>
        </div>
      )}

      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4 text-sm text-muted-foreground">
          <span><i className="fas fa-star mr-1"></i>+{mission.xpReward} XP</span>
          <span><i className="fas fa-coins mr-1"></i>+{mission.creditReward} Credits</span>
        </div>
        <Button
          onClick={() => startMissionMutation.mutate()}
          disabled={!canStart || startMissionMutation.isPending}
          size="sm"
          data-testid={`button-mission-action-${mission.id}`}
        >
          {isCompleted ? "Completed" : isInProgress ? "Continue" : "Start"}
        </Button>
      </div>
    </div>
  );
}

client/src/components/ProgressCard.tsx.content:
interface ProgressCardProps {
  track: string;
  completed: number;
  total: number;
  index: number;
}

const trackIcons = {
  EcoExplorer: "fas fa-seedling",
  ClimateChampion: "fas fa-cloud",
  WasteWarrior: "fas fa-recycle",
  GreenInnovator: "fas fa-lightbulb",
};

const trackColors = [
  "text-primary bg-primary/10",
  "text-secondary bg-secondary/10", 
  "text-accent bg-accent/10",
  "text-primary bg-primary/10",
];

export default function ProgressCard({ track, completed, total, index }: ProgressCardProps) {
  const percentage = total > 0 ? Math.round((completed / total) * 100) : 0;
  const colorClass = trackColors[index % trackColors.length];
  const iconClass = trackIcons[track as keyof typeof trackIcons] || "fas fa-leaf";

  return (
    <div className="bg-card rounded-lg p-6 border border-border shadow-sm hover:shadow-md transition-shadow" data-testid={`card-progress-${track}`}>
      <div className="flex items-center justify-between mb-4">
        <div className={`w-12 h-12 rounded-lg flex items-center justify-center ${colorClass}`}>
          <i className={`${iconClass} text-xl`}></i>
        </div>
        <div className="text-right">
          <div className="text-2xl font-bold text-primary" data-testid={`text-progress-${track}`}>
            {percentage}%
          </div>
          <div className="text-sm text-muted-foreground">Complete</div>
        </div>
      </div>
      <h3 className="font-semibold mb-2">{track}</h3>
      <div className="w-full bg-muted rounded-full h-2">
        <div 
          className="bg-primary h-2 rounded-full transition-all duration-500" 
          style={{ width: `${percentage}%` }}
        ></div>
      </div>
      <p className="text-sm text-muted-foreground mt-2" data-testid={`text-lessons-${track}`}>
        {completed}/{total} lessons complete
      </p>
    </div>
  );
}

