import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useEffect } from "react";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import ProgressCard from "@/components/ProgressCard";
import AchievementCard from "@/components/AchievementCard";
import LeaderboardCard from "@/components/LeaderboardCard";
import GuildCard from "@/components/GuildCard";
import MissionCard from "@/components/MissionCard";
import TensorflowARScanner from "@/components/TensorflowARScanner";
import { Button } from "@/components/ui/button";
import { Link } from "wouter";

export default function Dashboard() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();

  const { data: dashboardData, isLoading: isDashboardLoading } = useQuery({
    queryKey: ["/api/dashboard"],
    enabled: isAuthenticated && !isLoading,
    retry: false,
  });

  const { data: leaderboard } = useQuery({
    queryKey: ["/api/leaderboard"],
    queryFn: async () => {
      const response = await fetch("/api/leaderboard?period=weekly");
      if (!response.ok) throw new Error("Failed to fetch leaderboard");
      return response.json();
    },
    enabled: isAuthenticated && !isLoading,
  });

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  if (isLoading || isDashboardLoading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!user || !dashboardData) {
    return (
      <div className="text-center py-12">
        <p className="text-muted-foreground">Failed to load dashboard data</p>
      </div>
    );
  }

  const { trackProgress = [], achievements = [], missions = [] } = (dashboardData as any) || {};

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <section className="space-y-6">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
          <div>
            <h1 className="text-3xl font-bold" data-testid="text-welcome">
              Welcome back, {(user as any)?.firstName || "Explorer"}! ðŸŒ±
            </h1>
            <p className="text-muted-foreground">Ready to make an impact today?</p>
          </div>
          <div className="flex items-center space-x-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-primary" data-testid="text-user-level">
                Level {(user as any)?.level || 1}
              </div>
              <div className="text-sm text-muted-foreground">EcoChampion</div>
            </div>
          </div>
        </div>

        {/* Progress Overview Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {trackProgress.map((track: any, index: number) => (
            <ProgressCard
              key={track.track}
              track={track.track}
              completed={track.completed}
              total={track.total}
              index={index}
            />
          ))}
        </div>

        {/* Quick Actions & Today's Mission */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Today's Mission */}
          {missions.length > 0 && (
            <div className="lg:col-span-2">
              <MissionCard mission={missions[0]} isFeatured />
            </div>
          )}

          {/* Quick Actions */}
          <div className="bg-card rounded-lg border border-border p-6">
            <h3 className="text-lg font-semibold mb-4">Quick Actions</h3>
            <div className="space-y-3">
              <Link href="/ar-scanner" data-testid="link-ar-scanner-quick">
                <a className="w-full flex items-center justify-between p-3 bg-black text-white rounded-lg hover:bg-gray-800 transition-colors">
                  <div className="flex items-center space-x-3">
                    <i className="fas fa-camera text-white"></i>
                    <span>TensorFlow AR Scanner</span>
                  </div>
                  <i className="fas fa-chevron-right text-gray-300"></i>
                </a>
              </Link>

              <Link href="/simulation" data-testid="link-simulation-quick">
                <a className="w-full flex items-center justify-between p-3 bg-secondary/5 rounded-lg hover:bg-secondary/10 transition-colors">
                  <div className="flex items-center space-x-3">
                    <i className="fas fa-play text-secondary"></i>
                    <span>Eco Simulation</span>
                  </div>
                  <i className="fas fa-chevron-right text-muted-foreground"></i>
                </a>
              </Link>

              <Link href="/guilds" data-testid="link-guilds-quick">
                <a className="w-full flex items-center justify-between p-3 bg-accent/5 rounded-lg hover:bg-accent/10 transition-colors">
                  <div className="flex items-center space-x-3">
                    <i className="fas fa-users text-accent"></i>
                    <span>Join Guild</span>
                  </div>
                  <i className="fas fa-chevron-right text-muted-foreground"></i>
                </a>
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Achievements & Progress */}
      <section className="space-y-6">
        <div className="flex items-center justify-between">
          <h2 className="text-2xl font-bold">Recent Achievements</h2>
          <Link href="/profile#achievements" data-testid="link-view-all-achievements">
            <a className="text-primary hover:text-primary/80 font-medium">View All</a>
          </Link>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
          {achievements.slice(0, 4).map((achievement: any) => (
            <AchievementCard key={achievement.id} achievement={achievement} />
          ))}

          {achievements.length === 0 && (
            <div className="col-span-full text-center py-8 text-muted-foreground">
              <i className="fas fa-trophy text-4xl mb-4 opacity-50"></i>
              <p>Complete lessons and missions to unlock achievements!</p>
            </div>
          )}
        </div>
      </section>

      {/* Guild & Leaderboard */}
      <section className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <GuildCard user={user} />
        <LeaderboardCard leaderboard={leaderboard || []} currentUserId={(user as any)?.id || ''} />
      </section>
    </div>
  );
}

client/src/pages/Guilds.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function Guilds() {
  const { user } = useAuth();

  const { data: userGuild, isLoading: isGuildLoading } = useQuery({
    queryKey: ["/api/guild", (user as any)?.guildId],
    enabled: !!(user as any)?.guildId,
  });

  // Fetch available guilds from API
  const { data: availableGuilds = [], isLoading: isGuildsLoading } = useQuery({
    queryKey: ["/api/guilds"],
    enabled: !(user as any)?.guildId, // Only fetch if user doesn't have a guild
  });

  const { toast } = useToast();

  const joinGuildMutation = useMutation({
    mutationFn: async (guildId: string) => {
      const response = await apiRequest("POST", `/api/guild/${guildId}/join`);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Guild Joined!",
        description: "Welcome to your new guild! Start collaborating on environmental challenges.",
      });
      // Invalidate relevant queries to refresh UI
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/guild"] });
    },
    onError: (error) => {
      console.error("Failed to join guild:", error);
      toast({
        title: "Failed to Join Guild",
        description: "There was an error joining the guild. Please try again.",
        variant: "destructive",
      });
    },
  });

  if (isGuildLoading || isGuildsLoading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="text-center space-y-4">
        <h1 className="text-3xl font-bold">Environmental Guilds</h1>
        <p className="text-muted-foreground max-w-2xl mx-auto">
          Join a community of like-minded environmental champions. Collaborate on missions, 
          share knowledge, and make a greater impact together.
        </p>
      </div>

      {/* Current Guild Section */}
      {(user as any)?.guildId && userGuild ? (
        <section className="space-y-6">
          <h2 className="text-2xl font-bold">Your Guild</h2>
          <Card className="overflow-hidden">
            <div className="bg-gradient-to-r from-primary to-secondary p-6 text-white">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="text-xl font-bold mb-2" data-testid="text-current-guild-name">
                    {(userGuild as any)?.name}
                  </h3>
                  <p className="opacity-90">{(userGuild as any)?.description}</p>
                </div>
                <div className="text-right">
                  <div className="text-2xl font-bold" data-testid="text-current-guild-members">
                    {(userGuild as any)?.memberCount}
                  </div>
                  <div className="text-sm opacity-90">Members</div>
                </div>
              </div>
            </div>
            <CardContent className="p-6">
              {(userGuild as any)?.currentChallenge && (
                <div className="space-y-4">
                  <h4 className="font-semibold">Current Guild Challenge</h4>
                  <p className="text-muted-foreground" data-testid="text-current-guild-challenge">
                    {(userGuild as any)?.currentChallenge}
                  </p>
                  <div className="space-y-2">
                    <div className="flex justify-between text-sm">
                      <span>Progress</span>
                      <span data-testid="text-current-guild-progress">
                        {(userGuild as any)?.challengeProgress}/{(userGuild as any)?.challengeTarget}
                      </span>
                    </div>
                    <div className="w-full bg-muted rounded-full h-3">
                      <div 
                        className="bg-primary h-3 rounded-full transition-all duration-500" 
                        style={{ 
                          width: `${Math.round(((userGuild as any)?.challengeProgress / (userGuild as any)?.challengeTarget) * 100)}%` 
                        }}
                      ></div>
                    </div>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </section>
      ) : (
        /* Available Guilds Section */
        <section className="space-y-6">
          <h2 className="text-2xl font-bold">Available Guilds</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {availableGuilds.map((guild) => (
              <Card key={guild.id} className="hover:shadow-md transition-shadow" data-testid={`card-guild-${guild.id}`}>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg" data-testid={`text-guild-name-${guild.id}`}>
                      {guild.name}
                    </CardTitle>
                    <div className="flex items-center space-x-2">
                      <i className="fas fa-users text-muted-foreground"></i>
                      <span className="text-sm text-muted-foreground" data-testid={`text-guild-members-${guild.id}`}>
                        {guild.memberCount}
                      </span>
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  <p className="text-muted-foreground text-sm" data-testid={`text-guild-description-${guild.id}`}>
                    {guild.description}
                  </p>

                  <div className="space-y-2">
                    <div className="flex justify-between text-sm">
                      <span className="font-medium">Guild XP</span>
                      <span data-testid={`text-guild-xp-${guild.id}`}>
                        {guild.totalXP.toLocaleString()}
                      </span>
                    </div>
                  </div>

                  {guild.currentChallenge && (
                    <div className="space-y-2">
                      <h5 className="font-medium text-sm">Current Challenge</h5>
                      <p className="text-xs text-muted-foreground" data-testid={`text-guild-challenge-${guild.id}`}>
                        {guild.currentChallenge}
                      </p>
                      <div className="w-full bg-muted rounded-full h-2">
                        <div 
                          className="bg-primary h-2 rounded-full" 
                          style={{ 
                            width: `${Math.round((guild.challengeProgress / guild.challengeTarget) * 100)}%` 
                          }}
                        ></div>
                      </div>
                      <div className="text-xs text-muted-foreground">
                        {guild.challengeProgress}/{guild.challengeTarget} completed
                      </div>
                    </div>
                  )}

                  <Button 
                    className="w-full" 
                    data-testid={`button-join-guild-${guild.id}`}
                    onClick={() => joinGuildMutation.mutate(guild.id)}
                    disabled={joinGuildMutation.isPending}
                  >
                    {joinGuildMutation.isPending ? "Joining..." : "Join Guild"}
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        </section>
      )}

      {/* Guild Benefits */}
      <section className="bg-muted/50 rounded-lg p-6">
        <h3 className="text-xl font-bold mb-4">Guild Benefits</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="text-center">
            <div className="w-12 h-12 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-3">
              <i className="fas fa-users text-primary text-xl"></i>
            </div>
            <h4 className="font-semibold mb-2">Collaborative Learning</h4>
            <p className="text-sm text-muted-foreground">
              Work together on environmental challenges and share knowledge with fellow members.
            </p>
          </div>

          <div className="text-center">
            <div className="w-12 h-12 bg-secondary/10 rounded-full flex items-center justify-center mx-auto mb-3">
              <i className="fas fa-trophy text-secondary text-xl"></i>
            </div>
            <h4 className="font-semibold mb-2">Exclusive Challenges</h4>
            <p className="text-sm text-muted-foreground">
              Access guild-only missions and competitions with greater rewards and impact.
            </p>
          </div>

          <div className="text-center">
            <div className="w-12 h-12 bg-accent/10 rounded-full flex items-center justify-center mx-auto mb-3">
              <i className="fas fa-chart-line text-accent text-xl"></i>
            </div>
            <h4 className="font-semibold mb-2">Amplified Impact</h4>
            <p className="text-sm text-muted-foreground">
              Multiply your environmental impact through coordinated guild activities and projects.
            </p>
          </div>
        </div>
      </section>
    </div>
  );
}

client/src/pages/Learning.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import LessonCard from "@/components/LessonCard";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

const tracks = [
  {
    name: "EcoExplorer",
    description: "Discover biodiversity and ecosystem fundamentals",
    icon: "fas fa-seedling",
    color: "text-primary bg-primary/10",
  },
  {
    name: "ClimateChampion", 
    description: "Master climate science and solutions",
    icon: "fas fa-cloud",
    color: "text-secondary bg-secondary/10",
  },
  {
    name: "WasteWarrior",
    description: "Learn waste management and circular economy",
    icon: "fas fa-recycle", 
    color: "text-accent bg-accent/10",
  },
  {
    name: "GreenInnovator",
    description: "Explore sustainable technology and innovation",
    icon: "fas fa-lightbulb",
    color: "text-primary bg-primary/10",
  },
];

export default function Learning() {
  const [selectedTrack, setSelectedTrack] = useState("all");

  const { data: lessons, isLoading } = useQuery({
    queryKey: ["/api/lessons", selectedTrack === "all" ? undefined : selectedTrack],
    queryFn: async ({ queryKey }) => {
      const track = queryKey[1];
      const url = track ? `/api/lessons?track=${track}` : "/api/lessons";
      const response = await fetch(url);
      if (!response.ok) throw new Error("Failed to fetch lessons");
      return response.json();
    },
  });

  const { data: progress } = useQuery({
    queryKey: ["/api/progress"],
  });

  const progressMap = new Map((progress as any[])?.map((p: any) => [p.lessonId, p]) || []);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="text-center space-y-4">
        <h1 className="text-3xl font-bold">Environmental Learning Tracks</h1>
        <p className="text-muted-foreground max-w-2xl mx-auto">
          Choose your path to environmental mastery. Each track offers comprehensive lessons 
          designed to build your knowledge and earn valuable EcoCredits.
        </p>
      </div>

      {/* Track Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {tracks.map((track) => (
          <div 
            key={track.name}
            className="bg-card rounded-lg border border-border p-6 text-center hover:shadow-md transition-shadow cursor-pointer"
            onClick={() => setSelectedTrack(track.name)}
            data-testid={`card-track-${track.name}`}
          >
            <div className={`w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4 ${track.color}`}>
              <i className={`${track.icon} text-2xl`}></i>
            </div>
            <h3 className="font-semibold mb-2">{track.name}</h3>
            <p className="text-sm text-muted-foreground mb-4">{track.description}</p>
            <Button 
              variant={selectedTrack === track.name ? "default" : "outline"} 
              size="sm"
              data-testid={`button-select-track-${track.name}`}
            >
              {selectedTrack === track.name ? "Selected" : "Explore"}
            </Button>
          </div>
        ))}
      </div>

      {/* Lesson Filter Tabs */}
      <Tabs value={selectedTrack} onValueChange={setSelectedTrack} className="w-full">
        <TabsList className="grid w-full grid-cols-5" data-testid="tabs-lesson-filter">
          <TabsTrigger value="all" data-testid="tab-all-lessons">All Lessons</TabsTrigger>
          <TabsTrigger value="EcoExplorer" data-testid="tab-ecoexplorer">EcoExplorer</TabsTrigger>
          <TabsTrigger value="ClimateChampion" data-testid="tab-climatechampion">ClimateChampion</TabsTrigger>
          <TabsTrigger value="WasteWarrior" data-testid="tab-wastewarrior">WasteWarrior</TabsTrigger>
          <TabsTrigger value="GreenInnovator" data-testid="tab-greeninnovator">GreenInnovator</TabsTrigger>
        </TabsList>

        <TabsContent value={selectedTrack} className="mt-8">
          {lessons && lessons.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {lessons.map((lesson: any) => (
                <LessonCard 
                  key={lesson.id} 
                  lesson={lesson} 
                  progress={progressMap.get(lesson.id)}
                />
              ))}
            </div>
          ) : (
            <div className="text-center py-12">
              <i className="fas fa-book-open text-4xl text-muted-foreground mb-4"></i>
              <h3 className="text-lg font-semibold mb-2">No Lessons Available</h3>
              <p className="text-muted-foreground">
                {selectedTrack === "all" 
                  ? "No lessons have been created yet." 
                  : `No lessons available for ${selectedTrack} track.`
                }
              </p>
            </div>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/Missions.tsx.content:
import { useQuery } from "@tanstack/react-query";
import MissionCard from "@/components/MissionCard";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

export default function Missions() {
  const { data: missions, isLoading } = useQuery({
    queryKey: ["/api/missions"],
  });

  const { data: userMissions } = useQuery({
    queryKey: ["/api/dashboard"],
    select: (data) => (data as any)?.missions || [],
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  // Create mission map with user progress
  const userMissionMap = new Map((userMissions as any[])?.map((um: any) => [um.missionId, um]) || []);
  const enhancedMissions = (missions as any[])?.map((mission: any) => ({
    ...mission,
    ...userMissionMap.get(mission.id),
  })) || [];

  const dailyMissions = enhancedMissions.filter((m: any) => m.type === "daily");
  const weeklyMissions = enhancedMissions.filter((m: any) => m.type === "weekly");
  const specialMissions = enhancedMissions.filter((m: any) => m.type === "special");

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="text-center space-y-4">
        <h1 className="text-3xl font-bold">Environmental Missions</h1>
        <p className="text-muted-foreground max-w-2xl mx-auto">
          Take on challenges that make a real difference. Complete missions to earn XP, 
          EcoCredits, and contribute to environmental conservation efforts.
        </p>
      </div>

      {/* Mission Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-primary/5 rounded-lg p-6 text-center border border-primary/10">
          <div className="w-12 h-12 bg-primary rounded-full flex items-center justify-center mx-auto mb-3">
            <i className="fas fa-calendar-day text-primary-foreground"></i>
          </div>
          <h3 className="font-semibold mb-1">Daily Missions</h3>
          <p className="text-2xl font-bold text-primary" data-testid="text-daily-missions-count">
            {dailyMissions.length}
          </p>
          <p className="text-sm text-muted-foreground">Active today</p>
        </div>

        <div className="bg-secondary/5 rounded-lg p-6 text-center border border-secondary/10">
          <div className="w-12 h-12 bg-secondary rounded-full flex items-center justify-center mx-auto mb-3">
            <i className="fas fa-calendar-week text-secondary-foreground"></i>
          </div>
          <h3 className="font-semibold mb-1">Weekly Challenges</h3>
          <p className="text-2xl font-bold text-secondary" data-testid="text-weekly-missions-count">
            {weeklyMissions.length}
          </p>
          <p className="text-sm text-muted-foreground">This week</p>
        </div>

        <div className="bg-accent/5 rounded-lg p-6 text-center border border-accent/10">
          <div className="w-12 h-12 bg-accent rounded-full flex items-center justify-center mx-auto mb-3">
            <i className="fas fa-star text-accent-foreground"></i>
          </div>
          <h3 className="font-semibold mb-1">Special Events</h3>
          <p className="text-2xl font-bold text-accent" data-testid="text-special-missions-count">
            {specialMissions.length}
          </p>
          <p className="text-sm text-muted-foreground">Limited time</p>
        </div>
      </div>

      {/* Mission Tabs */}
      <Tabs defaultValue="all" className="w-full">
        <TabsList className="grid w-full grid-cols-4" data-testid="tabs-mission-filter">
          <TabsTrigger value="all" data-testid="tab-all-missions">All Missions</TabsTrigger>
          <TabsTrigger value="daily" data-testid="tab-daily-missions">Daily</TabsTrigger>
          <TabsTrigger value="weekly" data-testid="tab-weekly-missions">Weekly</TabsTrigger>
          <TabsTrigger value="special" data-testid="tab-special-missions">Special</TabsTrigger>
        </TabsList>

        <TabsContent value="all" className="mt-8">
          <div className="space-y-6">
            {enhancedMissions.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {enhancedMissions.map((mission: any) => (
                  <MissionCard key={mission.id} mission={mission} />
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <i className="fas fa-flag text-4xl text-muted-foreground mb-4"></i>
                <h3 className="text-lg font-semibold mb-2">No Missions Available</h3>
                <p className="text-muted-foreground">Check back later for new environmental challenges!</p>
              </div>
            )}
          </div>
        </TabsContent>

        <TabsContent value="daily" className="mt-8">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {dailyMissions.length > 0 ? (
              dailyMissions.map((mission: any) => (
                <MissionCard key={mission.id} mission={mission} />
              ))
            ) : (
              <div className="col-span-2 text-center py-12">
                <i className="fas fa-calendar-day text-4xl text-muted-foreground mb-4"></i>
                <h3 className="text-lg font-semibold mb-2">No Daily Missions</h3>
                <p className="text-muted-foreground">New daily missions are added regularly!</p>
              </div>
            )}
          </div>
        </TabsContent>

        <TabsContent value="weekly" className="mt-8">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {weeklyMissions.length > 0 ? (
              weeklyMissions.map((mission: any) => (
                <MissionCard key={mission.id} mission={mission} />
              ))
            ) : (
              <div className="col-span-2 text-center py-12">
                <i className="fas fa-calendar-week text-4xl text-muted-foreground mb-4"></i>
                <h3 className="text-lg font-semibold mb-2">No Weekly Challenges</h3>
                <p className="text-muted-foreground">Weekly challenges refresh every Monday!</p>
              </div>
            )}
          </div>
        </TabsContent>

        <TabsContent value="special" className="mt-8">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {specialMissions.length > 0 ? (
              specialMissions.map((mission: any) => (
                <MissionCard key={mission.id} mission={mission} />
              ))
            ) : (
              <div className="col-span-2 text-center py-12">
                <i className="fas fa-star text-4xl text-muted-foreground mb-4"></i>
                <h3 className="text-lg font-semibold mb-2">No Special Events</h3>
                <p className="text-muted-foreground">Keep an eye out for limited-time special missions!</p>
              </div>
            )}
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/Profile.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useEffect } from "react";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import AchievementCard from "@/components/AchievementCard";

export default function Profile() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();

  const { data: achievements, isLoading: achievementsLoading } = useQuery({
    queryKey: ["/api/achievements"],
    enabled: isAuthenticated && !isLoading,
    retry: false,
  });

  const { data: progress } = useQuery({
    queryKey: ["/api/progress"],
    enabled: isAuthenticated && !isLoading,
    retry: false,
  });

  const { data: ecoActions } = useQuery({
    queryKey: ["/api/eco-actions"],
    enabled: isAuthenticated && !isLoading,
    retry: false,
  });

  const { data: userGuild } = useQuery({
    queryKey: ["/api/guild", (user as any)?.guildId],
    enabled: isAuthenticated && !isLoading && !!(user as any)?.guildId,
    retry: false,
  });

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  if (isLoading || achievementsLoading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="text-center py-12">
        <p className="text-muted-foreground">Failed to load profile data</p>
      </div>
    );
  }

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  const completedLessons = (progress as any[])?.filter((p: any) => p.completed).length || 0;
  const totalActions = (ecoActions as any[])?.length || 0;
  const totalAchievements = (achievements as any[])?.length || 0;

  // Calculate level progress (1000 XP per level)
  const currentLevelXP = ((user as any)?.xp || 0) % 1000;
  const levelProgress = (currentLevelXP / 1000) * 100;

  return (
    <div className="space-y-8">
      {/* Profile Header */}
      <Card>
        <CardContent className="p-8">
          <div className="flex flex-col md:flex-row items-center md:items-start gap-6">
            {/* Avatar */}
            <div className="relative">
              <div className="w-24 h-24 bg-primary rounded-full flex items-center justify-center">
                {(user as any)?.profileImageUrl ? (
                  <img 
                    src={(user as any).profileImageUrl} 
                    alt="Profile" 
                    className="w-24 h-24 rounded-full object-cover"
                    data-testid="img-profile-avatar"
                  />
                ) : (
                  <i className="fas fa-user text-4xl text-primary-foreground"></i>
                )}
              </div>
              <div className="absolute -bottom-2 -right-2 w-8 h-8 bg-accent rounded-full flex items-center justify-center border-2 border-background">
                <span className="text-sm font-bold text-accent-foreground" data-testid="text-user-level-badge">
                  {(user as any)?.level || 1}
                </span>
              </div>
            </div>

            {/* User Info */}
            <div className="flex-1 text-center md:text-left">
              <h1 className="text-3xl font-bold mb-2" data-testid="text-user-name">
                {(user as any)?.firstName} {(user as any)?.lastName}
              </h1>
              {(user as any)?.email && (
                <p className="text-muted-foreground mb-4" data-testid="text-user-email">
                  {(user as any)?.email}
                </p>
              )}

              {/* Level Progress */}
              <div className="mb-4">
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm font-medium">Level {(user as any)?.level || 1} Progress</span>
                  <span className="text-sm text-muted-foreground">
                    {currentLevelXP}/1000 XP
                  </span>
                </div>
                <div className="w-full bg-muted rounded-full h-2">
                  <div 
                    className="bg-primary h-2 rounded-full transition-all duration-500" 
                    style={{ width: `${levelProgress}%` }}
                  ></div>
                </div>
              </div>

              {/* Stats */}
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
                <div className="text-center">
                  <div className="text-2xl font-bold text-primary" data-testid="text-stat-xp">
                    {((user as any)?.xp || 0).toLocaleString()}
                  </div>
                  <div className="text-sm text-muted-foreground">Total XP</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-accent" data-testid="text-stat-credits">
                    {((user as any)?.ecoCredits || 0).toLocaleString()}
                  </div>
                  <div className="text-sm text-muted-foreground">EcoCredits</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-secondary" data-testid="text-stat-lessons">
                    {completedLessons}
                  </div>
                  <div className="text-sm text-muted-foreground">Lessons</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-primary" data-testid="text-stat-actions">
                    {totalActions}
                  </div>
                  <div className="text-sm text-muted-foreground">Eco Actions</div>
                </div>
              </div>

              {/* Guild Information */}
              {(user as any)?.guildId && userGuild && (
                <div className="mb-6">
                  <h3 className="text-lg font-semibold mb-2">Guild Membership</h3>
                  <div className="bg-muted/50 rounded-lg p-4">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-primary rounded-full flex items-center justify-center">
                        <i className="fas fa-users text-white"></i>
                      </div>
                      <div>
                        <div className="font-semibold" data-testid="text-profile-guild">
                          {(userGuild as any)?.name}
                        </div>
                        <div className="text-sm text-muted-foreground">
                          {(userGuild as any)?.memberCount} members
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* Action Buttons */}
              <div className="flex flex-col sm:flex-row gap-2">
                <Button variant="outline" data-testid="button-edit-profile">
                  <i className="fas fa-edit mr-2"></i>
                  Edit Profile
                </Button>
                <Button variant="destructive" onClick={handleLogout} data-testid="button-logout">
                  <i className="fas fa-sign-out-alt mr-2"></i>
                  Logout
                </Button>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Profile Tabs */}
      <Tabs defaultValue="achievements" className="w-full">
        <TabsList className="grid w-full grid-cols-3" data-testid="tabs-profile">
          <TabsTrigger value="achievements" data-testid="tab-achievements">Achievements</TabsTrigger>
          <TabsTrigger value="progress" data-testid="tab-progress">Progress</TabsTrigger>
          <TabsTrigger value="eco-actions" data-testid="tab-eco-actions">Eco Actions</TabsTrigger>
        </TabsList>

        {/* Achievements Tab */}
        <TabsContent value="achievements" className="mt-8">
          <div className="space-y-6">
            <div className="flex items-center justify-between">
              <h2 className="text-2xl font-bold">Your Achievements</h2>
              <Badge variant="outline" className="text-lg px-3 py-1" data-testid="badge-achievement-count">
                {totalAchievements} Unlocked
              </Badge>
            </div>

            {achievements && (achievements as any[]).length > 0 ? (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
                {(achievements as any[]).map((achievement: any) => (
                  <AchievementCard key={achievement.id} achievement={achievement} />
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <i className="fas fa-trophy text-4xl text-muted-foreground mb-4"></i>
                <h3 className="text-lg font-semibold mb-2">No Achievements Yet</h3>
                <p className="text-muted-foreground">
                  Complete lessons and missions to unlock your first achievement!
                </p>
              </div>
            )}
          </div>
        </TabsContent>

        {/* Progress Tab */}
        <TabsContent value="progress" className="mt-8">
          <div className="space-y-6">
            <h2 className="text-2xl font-bold">Learning Progress</h2>

            {progress && (progress as any[]).length > 0 ? (
              <div className="space-y-4">
                {(progress as any[])
                  .filter((p: any) => p.completed)
                  .sort((a: any, b: any) => new Date(b.completedAt).getTime() - new Date(a.completedAt).getTime())
                  .map((p: any) => (
                    <Card key={`${p.userId}-${p.lessonId}`} className="p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <h4 className="font-semibold" data-testid={`text-progress-lesson-${p.lessonId}`}>
                            Lesson #{p.lessonId}
                          </h4>
                          <p className="text-sm text-muted-foreground">
                            Completed {new Date(p.completedAt).toLocaleDateString()}
                          </p>
                        </div>
                        <div className="flex items-center space-x-4">
                          {p.score && (
                            <Badge 
                              className={
                                p.score >= 90 ? "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200" :
                                p.score >= 80 ? "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200" :
                                "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200"
                              }
                              data-testid={`badge-score-${p.lessonId}`}
                            >
                              {p.score}%
                            </Badge>
                          )}
                          <i className="fas fa-check text-green-600"></i>
                        </div>
                      </div>
                    </Card>
                  ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <i className="fas fa-book-open text-4xl text-muted-foreground mb-4"></i>
                <h3 className="text-lg font-semibold mb-2">No Completed Lessons</h3>
                <p className="text-muted-foreground">
                  Start learning to see your progress here!
                </p>
              </div>
            )}
          </div>
        </TabsContent>

        {/* Eco Actions Tab */}
        <TabsContent value="eco-actions" className="mt-8">
          <div className="space-y-6">
            <h2 className="text-2xl font-bold">Your Eco Actions</h2>

            {ecoActions && (ecoActions as any[]).length > 0 ? (
              <div className="space-y-4">
                {(ecoActions as any[]).map((action: any) => (
                  <Card key={action.id} className="p-4">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center space-x-2 mb-2">
                          <Badge variant="outline" className={
                            action.type === "ar_scan" ? "bg-primary/10 text-primary" :
                            action.type === "simulation_action" ? "bg-secondary/10 text-secondary" :
                            "bg-accent/10 text-accent"
                          }>
                            {action.type.replace('_', ' ').toUpperCase()}
                          </Badge>
                          {action.verified && (
                            <Badge className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
                              <i className="fas fa-check mr-1"></i>
                              Verified
                            </Badge>
                          )}
                        </div>
                        <h4 className="font-semibold mb-1" data-testid={`text-action-description-${action.id}`}>
                          {action.description}
                        </h4>
                        <p className="text-sm text-muted-foreground">
                          {new Date(action.createdAt).toLocaleDateString()} at{' '}
                          {new Date(action.createdAt).toLocaleTimeString()}
                        </p>
                      </div>
                      <div className="text-right">
                        <div className="text-sm text-muted-foreground mb-1">Rewards</div>
                        <div className="flex flex-col space-y-1">
                          {action.xpEarned > 0 && (
                            <span className="text-sm font-medium" data-testid={`text-action-xp-${action.id}`}>
                              +{action.xpEarned} XP
                            </span>
                          )}
                          {action.creditsEarned > 0 && (
                            <span className="text-sm font-medium text-accent" data-testid={`text-action-credits-${action.id}`}>
                              +{action.creditsEarned} Credits
                            </span>
                          )}
                        </div>
                      </div>
                    </div>
                  </Card>
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <i className="fas fa-leaf text-4xl text-muted-foreground mb-4"></i>
                <h3 className="text-lg font-semibold mb-2">No Eco Actions Yet</h3>
                <p className="text-muted-foreground">
                  Use the AR scanner or participate in simulations to track your environmental impact!
                </p>
              </div>
            )}
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/Simulation.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useEffect } from "react";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import SimulationInterface from "@/components/SimulationInterface";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

export default function Simulation() {
  const { isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const [isSimulationRunning, setIsSimulationRunning] = useState(false);
  const [simulationResults, setSimulationResults] = useState<any>(null);

  const saveScenarioMutation = useMutation({
    mutationFn: async (scenarioData: any) => {
      const response = await apiRequest("POST", "/api/eco-actions", scenarioData);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Scenario Saved!",
        description: `You earned ${data.xpEarned || 75} XP and ${data.creditsEarned || 15} EcoCredits for creating an environmental scenario.`,
      });
    },
    onError: (error) => {
      console.error("Failed to save scenario:", error);
      toast({
        title: "Failed to Save Scenario",
        description: "There was an error saving your simulation scenario. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  const handleSimulationComplete = (results: any) => {
    setSimulationResults(results);
    setIsSimulationRunning(false);
  };

  const handleSaveScenario = () => {
    const scenarioData = {
      type: "simulation_action",
      description: "Created and saved an environmental simulation scenario",
      xpEarned: 75,
      creditsEarned: 15,
    };
    saveScenarioMutation.mutate(scenarioData);
  };

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="text-center space-y-4">
        <h1 className="text-3xl font-bold">Ecosystem Simulation</h1>
        <p className="text-muted-foreground max-w-2xl mx-auto">
          Explore the complex relationships within ecosystems. Make changes to environmental 
          factors and observe their real-time impact on biodiversity, climate, and sustainability.
        </p>
      </div>

      {/* Simulation Controls */}
      <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
        <div className="lg:col-span-1 space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Quick Actions</CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <Button 
                onClick={() => setIsSimulationRunning(true)}
                disabled={isSimulationRunning}
                className="w-full"
                data-testid="button-start-simulation"
              >
                <i className="fas fa-play mr-2"></i>
                {isSimulationRunning ? "Running..." : "Start Simulation"}
              </Button>

              <Button variant="outline" className="w-full" data-testid="button-reset-simulation">
                <i className="fas fa-refresh mr-2"></i>
                Reset Environment
              </Button>

              <Button 
                variant="outline" 
                className="w-full" 
                data-testid="button-save-scenario"
                onClick={handleSaveScenario}
                disabled={saveScenarioMutation.isPending}
              >
                <i className="fas fa-save mr-2"></i>
                {saveScenarioMutation.isPending ? "Saving..." : "Save Scenario"}
              </Button>
            </CardContent>
          </Card>

          {/* Simulation Presets */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Scenarios</CardTitle>
            </CardHeader>
            <CardContent className="space-y-2">
              <Button variant="ghost" size="sm" className="w-full justify-start" data-testid="button-scenario-deforestation">
                ðŸŒ³ Deforestation Impact
              </Button>
              <Button variant="ghost" size="sm" className="w-full justify-start" data-testid="button-scenario-climate">
                ðŸŒ¡ï¸ Climate Change
              </Button>
              <Button variant="ghost" size="sm" className="w-full justify-start" data-testid="button-scenario-renewable">
                âš¡ Renewable Energy
              </Button>
              <Button variant="ghost" size="sm" className="w-full justify-start" data-testid="button-scenario-conservation">
                ðŸ¦‹ Conservation Efforts
              </Button>
            </CardContent>
          </Card>
        </div>

        {/* Main Simulation Interface */}
        <div className="lg:col-span-3">
          <SimulationInterface 
            isRunning={isSimulationRunning}
            onComplete={handleSimulationComplete}
            onStop={() => setIsSimulationRunning(false)}
          />
        </div>
      </div>

      {/* Simulation Results */}
      {simulationResults && (
        <Card>
          <CardHeader>
            <CardTitle>Simulation Results</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {/* Environmental Impact */}
              <div className="space-y-4">
                <h4 className="font-semibold">Environmental Impact</h4>
                <div className="space-y-2">
                  <div className="flex justify-between">
                    <span className="text-sm">Biodiversity Change</span>
                    <Badge 
                      className={simulationResults.biodiversityChange >= 0 ? 
                        "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200" :
                        "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200"
                      }
                      data-testid="badge-biodiversity-change"
                    >
                      {simulationResults.biodiversityChange >= 0 ? "+" : ""}
                      {simulationResults.biodiversityChange}%
                    </Badge>
                  </div>

                  <div className="flex justify-between">
                    <span className="text-sm">Carbon Emissions</span>
                    <Badge 
                      className={simulationResults.carbonChange <= 0 ? 
                        "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200" :
                        "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200"
                      }
                      data-testid="badge-carbon-change"
                    >
                      {simulationResults.carbonChange >= 0 ? "+" : ""}
                      {simulationResults.carbonChange}%
                    </Badge>
                  </div>

                  <div className="flex justify-between">
                    <span className="text-sm">Temperature Change</span>
                    <Badge 
                      className={simulationResults.temperatureChange <= 0 ? 
                        "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200" :
                        "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200"
                      }
                      data-testid="badge-temperature-change"
                    >
                      {simulationResults.temperatureChange >= 0 ? "+" : ""}
                      {simulationResults.temperatureChange}Â°C
                    </Badge>
                  </div>
                </div>
              </div>

              {/* Economic Impact */}
              <div className="space-y-4">
                <h4 className="font-semibold">Economic Impact</h4>
                <div className="space-y-2">
                  <div className="flex justify-between">
                    <span className="text-sm">Sustainability Index</span>
                    <span className="font-medium" data-testid="text-sustainability-index">
                      {simulationResults.sustainabilityIndex}/100
                    </span>
                  </div>

                  <div className="flex justify-between">
                    <span className="text-sm">Economic Value</span>
                    <span className="font-medium" data-testid="text-economic-value">
                      ${simulationResults.economicValue?.toLocaleString() || 0}
                    </span>
                  </div>
                </div>
              </div>

              {/* Rewards */}
              <div className="space-y-4">
                <h4 className="font-semibold">Your Rewards</h4>
                <div className="space-y-2">
                  <div className="flex justify-between">
                    <span className="text-sm">XP Earned</span>
                    <span className="font-medium text-primary" data-testid="text-rewards-xp">
                      +{simulationResults.xpEarned || 0}
                    </span>
                  </div>

                  <div className="flex justify-between">
                    <span className="text-sm">EcoCredits</span>
                    <span className="font-medium text-accent" data-testid="text-rewards-credits">
                      +{simulationResults.creditsEarned || 0}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Educational Content */}
      <Card>
        <CardHeader>
          <CardTitle>Learn More</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            <div className="text-center">
              <div className="w-12 h-12 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-3">
                <i className="fas fa-seedling text-primary text-xl"></i>
